function toggleMenu(){const menuMobile=document.querySelector(".menu-mobile");if(menuMobile.style.display==="block"){menuMobile.style.display="none"}else{menuMobile.style.display="block"}}function openModal(){document.getElementById("buymodal").style.display="flex"}function closeModal(){document.getElementById("buymodal").style.display="none"}function openCallModal(){const callModal=document.getElementById("callModal");if(callModal){callModal.classList.add("show");document.body.style.overflow="hidden"}}function closeCallModal(){const callModal=document.getElementById("callModal");if(callModal){callModal.classList.remove("show");document.body.style.overflow=""}}document.addEventListener("DOMContentLoaded",function(){const callModalForm=document.querySelector(".modal-form");if(callModalForm){callModalForm.addEventListener("submit",async e=>{e.preventDefault();const formData={name:e.target.querySelector('input[type="text"]').value,phone:e.target.querySelector('input[type="tel"]').value};try{const response=await fetch("/submit-form",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify(formData)});if(response.ok){alert("Заявка успешно отправлена!");closeCallModal()}else{alert("Произошла ошибка при отправке заявки.")}}catch(error){console.error("Ошибка:",error);alert("Не удалось отправить заявку. Попробуйте позже.")}})}const ctaForm=document.querySelector(".cta__form");if(ctaForm){ctaForm.addEventListener("submit",async e=>{e.preventDefault();const formData={name:document.querySelector('.cta__form input[type="text"]').value,phone:document.querySelector('.cta__form input[type="tel"]').value,comment:document.querySelector(".cta__form textarea").value};try{const response=await fetch("/submit-cta-form",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify(formData)});if(response.ok){alert("Заявка успешно отправлена!");document.querySelector(".cta__form").reset()}else{alert("Произошла ошибка при отправке заявки.")}}catch(error){console.error("Ошибка:",error);alert("Не удалось отправить заявку. Попробуйте позже.")}})}});function openConsultationModal(){const consultationModal=document.getElementById("consultationModal");if(consultationModal){consultationModal.style.display="flex"}}function closeConsultationModal(){const consultationModal=document.getElementById("consultationModal");if(consultationModal){consultationModal.style.display="none"}}window.onclick=function(event){const callModal=document.getElementById("callModal");const consultationModal=document.getElementById("consultationModal");if(event.target===callModal){closeCallModal()}if(event.target===consultationModal){closeConsultationModal()}};document.addEventListener("DOMContentLoaded",function(){const cards=document.querySelectorAll(".hero-card");cards.forEach((card,index)=>{setTimeout(()=>{card.classList.add("show")},index*300)})});function openBuyModal(){openCallModal()}function closeBuyModal(){closeCallModal()}function openPromoModalManually(){console.log("Ручное открытие акционного модального окна");const modal=document.getElementById("promoModal");if(!modal){console.error("Модальное окно не найдено");return}modal.style.display="block";setTimeout(()=>{modal.classList.add("active")},10)}